      var persons = null;
      if(this.state.showPersons){
        persons = (
            <div>{this.state.person.map((person, index) => <Person name = {person.name}
              age = {person.age} click = {this.deletePerson.bind(index)}/>)}
            </div>
        );
      }
      return (
      <div className="App">
        <h1>Hi I am react app</h1>
        <p>This is really working</p>

        <button style = {style} onClick = {this.togglePersons}>Switch</button>
        {persons}

