import React, {Component} from "react";
import "./Person.css"
class Person extends  Component{
    render(){
        return (
            <div className = "Person" onClick = {this.props.click} >
                <h1>I am {this.props.name} and I am {this.props.age}</h1>
                <input className = "centeredText" type = "text" value = {this.props.name} onChange = {this.props.changed}></input>
            </div>
        );
    }

};

export default person;

/*Above we have used two way binding i.e we have provided value as well as we are also changing the value*/
