        person:[
          {name: newName, age: "19"},
          {name: "Jai", age: "22"},
          {name: "Piyush", age: "20"}
        ]
      })
    };

    changeName = (e) =>{
      this.setState({
        person:[
          {name: e.target.value, age: "19"},
          {name: "Jai", age: "22"},
          {name: "Piyush", age: "20"}
        ]
      })
