import React, {Component} from 'react'
import Post from '../../components/Post/Post'
import FullPost from '../FullPost/FullPost'
import NewPost from '../NewPost/NewPost'
import axios from "axios"
import styles from "./Blog.module.css"
import Posts from "./../Posts/Posts"
import {Route, Link} from "react-router-dom"

class Blog extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fullPost: null
        };
    };
    setFullPostHandler = (id) => {
        this.setState({fullPost: id});
    };

    render() {
        //console.log(styles.Posts)
        return (
            <div className = {styles.Blog}>
                <header>
                    <nav>
                        <ul>
                            <li>
                                <Link to = "/">Home</Link>
                            </li>
                            <li>
                                <Link to = {{pathname: "/new-post",
                            hash: "#submit",
                            search: "?quick-submit=true"}}>New Post</Link>
                            </li>
                        </ul>
                    </nav>
                </header>
                {/*<Route path = "/" exact render = {()=> <h3>Home</h3>}></Route>*/}
                <Route path = "/" exact component = {Posts}></Route>
                <Route path = "/new-post" exact component = {NewPost}></Route>
            </div>
        );
    }
}

export default Blog;

//link tag do not let reload your site as in the case of a tag, it uses to prop for defining the location to which they 
//should navigate to this can either be a string or a location object
